import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { formatDistanceToNow } from 'date-fns';

function edit(el) {
  console.log(el);
  this.setState(({ edit }) => ({ edit: !edit }));
}

function editText(el) {
  el.preventDefault();
  el.target[0].value = this.task;
  let text = el.target[0].value;
  if (text.length > 0) {
    this.props.editTask(text);
  }
  this.setState(() => ({ edit: false }));
}

function pauseTimer() {
  this.setState({ paused: true });
}
function playTimer() {
  this.setState({ paused: false });
}

function timerTask() {
  let { timer } = this.state;
  let minutes = Math.floor(timer / (1000 * 60));
  let seconds = Math.floor((timer % (1000 * 60)) / 1000);

  if (timer > 0) {
    return `  ${String(minutes).padStart(2, '0')} : ${String(seconds).padStart(2, '0')}`;
  } else {
    return '  0:0';
  }
}

function TaskText(edit) {
  if (edit) {
    return (
      <label htmlFor={this.props.id}>
        <form onSubmit={editText.bind(this)}>
          <input className="description" value={this.task} autoFocus></input>
        </form>
      </label>
    );
  } else
    return (
      <label htmlFor={this.props.id}>
        <span className="title">{this.task}</span>
        <span className="description">
          <button className="icon icon-play" onClick={playTimer.bind(this)}></button>
          <button className="icon icon-pause" onClick={pauseTimer.bind(this)}></button>
          {timerTask.call(this)}
        </span>
        <span className="description">created {formatDistanceToNow(this.date)} ago</span>
      </label>
    );
}

export default class Task extends Component {
  task = '';



  componentDidMount() {
    this.timer = setInterval(() => {
      this.setState(({ paused }) => ({
        date: this.date,
        timer: paused ? this.state.timer : this.state.timer - 1000,
      }));
    }, 1000);
  }

  componentWillUnmount() {
    clearInterval(this.timer);
  }

  render() {
    const { id, task, date, completed, paused, timer, onDeleted, setCompleted, edit, toggleEdit, editTask, playTimer, pauseTimer } = this.props;
    const minutes = Math.floor(timer / (1000 * 60));
    const seconds = Math.floor((timer % (1000 * 60)) / 1000);

    return (
      <li className={completed ? 'completed' : null}>
        <div className="view">
          <input
            className="toggle"
            checked={completed}
            id={id}
            type="checkbox"
            onChange={() => setCompleted(id)}
          />
          {edit ? (
            <label htmlFor={id}>
              <form
                onSubmit={(e) => {
                  e.preventDefault();
                  editTask(id, e.target[0].value);
                }}
              >
                <input className="description" defaultValue={task} autoFocus />
              </form>
            </label>
          ) : (
            <label htmlFor={id}>
              <span className="title">{task}</span>
              <span className="description">
                <button className="icon icon-play" onClick={() => playTimer(id)}></button>
                <button className="icon icon-pause" onClick={() => pauseTimer(id)}></button>
                {`  ${String(minutes).padStart(2, '0')} : ${String(seconds).padStart(2, '0')}`}
              </span>
              <span className="description">created {formatDistanceToNow(date)} ago</span>
            </label>
          )}
          <button className="icon icon-edit" onClick={() => toggleEdit(id)} />
          <button onClick={() => onDeleted(id)} className="icon icon-destroy" />
        </div>
      </li>
    );
  }
}

Task.propTypes = {
  id: PropTypes.number.isRequired,
  task: PropTypes.string.isRequired,
  date: PropTypes.number.isRequired,
  completed: PropTypes.bool,
  paused: PropTypes.bool,
  timer: PropTypes.number.isRequired,
  edit: PropTypes.bool,
  onDeleted: PropTypes.func.isRequired,
  setCompleted: PropTypes.func.isRequired,
  editTask: PropTypes.func.isRequired,
  toggleEdit: PropTypes.func.isRequired,
  playTimer: PropTypes.func.isRequired,
  pauseTimer: PropTypes.func.isRequired,
  updateTimer: PropTypes.func.isRequired,
};
